        /// <summary>
        /// This is the elegant solution
        /// </summary>
        /// <param name="input">String sequence of '(' and ')' characters</param>
        private static void CalculateFloor1(string input)
        {
            List<char> list = input.ToList<char>();
            FloorNumber = 0;

            list.ForEach(s => FloorNumber = s == '(' ? FloorNumber + 1 : FloorNumber - 1);
            
            Console.WriteLine("The instructions take Santa to floor: " + FloorNumber.ToString());   
        }

        /// <summary>
        /// This is the performatic solution
        /// </summary>
        /// <param name="input">String sequence of '(' and ')' characters</param>
        private static void CalculateFloor2(string input)
        {
            char[] list = input.ToCharArray();
            FloorNumber = 0;

            for (int i = 0; i < list.Length; i++)
            {
                if (list[i] == '(')
                    FloorNumber++;
                else if (list[i] == ')')
                    FloorNumber--;
            }

            Console.WriteLine("The instructions take Santa to floor: " + FloorNumber.ToString());
        }
