        private static void CalculateInput(string input, bool getBasementPosition)
        {
            char[] list = input.ToCharArray();
            FloorNumber = 0;
            BasementPosition = 0;

            for (int i = 0; i < list.Length; i++)
            {
                if (list[i] == '(')
                    FloorNumber++;
                else if (list[i] == ')')
                    FloorNumber--;
                if (getBasementPosition)
                {
                    if (FloorNumber == -1)
                    {
                        BasementPosition = i + 1;
                        break;
                    }
                }
            }

            string message = getBasementPosition ? "The position that takes Santa to basement is : " + BasementPosition.ToString() : "The instructions take Santa to floor: " + FloorNumber.ToString();
            Console.WriteLine(message);
        }
